package Seedstuff;
/**
 * Die Klasse {@code Seedreader} dient dazu, aus einem statischen Seed-String
 * deterministische Zufallswerte für das Spiel zu generieren.
 * Sie liest Ziffern aus dem Seed und gibt diese als unterschiedliche Werte aus.
 */
public class Seedreader {
    /** Der statische Seed-String, aus dem die Zufallswerte gelesen werden */

    public static final String Ursseed = "";
 //länge: 32768
    int position = 1;
    /**
     * Gibt die nächste Ziffer aus dem Seed als Zahl zwischen 0 und 9 zurück.
     * Die Leseposition wird dabei um 1 erhöht.
     *
     * @return Eine Zahl zwischen 0 und 9
     */
    public int zeroninereader(){
        char digit = Ursseed.charAt(position);
        position = position + 1;                //Increase Seed by one
        int output = digit - '0';               //change type to int
        return output;
    }
    /**
     * Gibt einen zufälligen Wahrheitswert (true/false) aus dem Seed zurück.
     * Ziffern größer als 4 führen zu {@code true}, sonst {@code false}.
     * Die Leseposition wird dabei um 1 erhöht.
     *
     * @return {@code true} oder {@code false} basierend auf dem Seed
     */
    public boolean fiftyfiftyreader() {
        char digit = Ursseed.charAt(position);
        position = position + 1;                //Increase Seed by one
        int X = digit - '0';                    //change type to int

        boolean output;
        if (X > 4) {
            output = true;                      //turn digits into true or false
        } else {
            output = false;
        }
        return output;
    }

    /**
     * Gibt eine zweistellige Zahl (00 bis 99) aus dem Seed zurück.
     * Die Leseposition wird dabei um 2 erhöht.
     *
     * @return Eine Zahl zwischen 0 und 99
     */
    public int hundredreader(){
        char digit1 = Ursseed.charAt(position);
        position = position + 1;                //Increase Seed by one
        int X = digit1 - '0';                   //change type to int and save
        char digit2 = Ursseed.charAt(position);
        position = position + 1;                //Increase Seed by one
        int Y = digit2 - '0';                   //change type to int and save
        String s1 = Integer.toString(X);        //make Integers to String
        String s2 = Integer.toString(Y);
        String s = s1 + s2;                     // combine both strings
        int output = Integer.parseInt(s);       // turn combined string back into Integers
        return output;
    }
    /**
     * Setzt die Leseposition im Seed neu.
     *
     * @param Position Die neue Position im Seed-String
     */
    public void setPosition(int Position){
        position = Position;
    }
}